import React from 'react';
import ReactDOM from 'react-dom';

const Modal = ({ mostrar, fechar }) => mostrar ? ReactDOM.createPortal(
  <React.Fragment>
    <div className="modal_sobreposicao"/>
    <div className="modal_alinhamento" aria-modal aria-hidden tabIndex={-1} role="dialog">
      <div className="modal">
        <div className="modal_conteudo">
          <button type="button" className="fechar_modal" aria-label="Fechar" onClick={pressionado}>
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <p>
          Hello, I'm a modal.
        </p>
      </div>
    </div>
  </React.Fragment>, document.body
) : null;

export default Modal;
